<?php

namespace Brasa\CarteraBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CarConsecutivoRepository extends EntityRepository {
    /**
     * Sacar un consecutivo
     *
     * @author		Mario Estrada
     *
     * @param   integer	Codigo del consecutivo
     */
    public function consecutivo($codigoConsecutivo) {
        $em = $this->getEntityManager();
        $intNumero = 0;
        $arConsecutivo = new \Brasa\CarteraBundle\Entity\CarConsecutivo();
        $arConsecutivo = $em->getRepository('BrasaCarteraBundle:CarConsecutivo')->find($codigoConsecutivo);     
        $intNumero = $arConsecutivo->getConsecutivo();
        $arConsecutivo->setConsecutivo($intNumero + 1);
        $em->persist($arConsecutivo);
        $em->flush();
        return $intNumero;
    }        
    
}