<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuDisciplinarioRepository extends EntityRepository {
    
    public function listaDQL($strIdentificacion = "", $codigoCentroCosto = "", $codigoZona = "", $codigoOperacion = "", $estadoCerrado = "", $estadoProcede = "", $strDesde = "", $strHasta = "") {        
        $dql   = "SELECT d, e FROM BrasaRecursoHumanoBundle:RhuDisciplinario d JOIN d.empleadoRel e WHERE d.codigoDisciplinarioPk <> 0";
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion LIKE '%" . $strIdentificacion . "%'";
        }
        if($codigoCentroCosto != "" || $codigoCentroCosto != 0 ) {
            $dql .= " AND e.codigoCentroCostoFk = " . $codigoCentroCosto;
        }
        if($codigoZona != "" || $codigoZona != 0 ) {
            $dql .= " AND e.codigoZonaFk = " . $codigoZona;
        }
        if($codigoOperacion != "" || $codigoOperacion != 0 ) {
            $dql .= " AND e.codigoSubzonaFk = " . $codigoOperacion;
        }
        if($estadoCerrado == 1 ) {
            $dql .= " AND d.estadoCerrado = 1";
        }
        if($estadoCerrado == "0" ) {
            $dql .= " AND d.estadoCerrado = 0";
        }
        if($estadoProcede == 1 ) {
            $dql .= " AND d.estadoProcede = 1";
        }
        if($estadoProcede == "0" ) {
            $dql .= " AND d.estadoProcede = 0";
        }
        if ($strDesde != ""){
            $dql .= " AND d.fecha >='" . $strDesde . "'";
        }
        if($strHasta != "") {
            $dql .= " AND d.fecha <='" .  $strHasta . "'";
        }
        $dql .= " ORDER BY d.fecha";
        return $dql;
    }       
    
    public function fechaAntigua() {        
        $em = $this->getEntityManager();
        $dql   = "SELECT min(d.fecha) FROM BrasaRecursoHumanoBundle:RhuDisciplinario d";
        $query = $em->createQuery($dql);
        $fechaAntigua = $query->getSingleScalarResult(); 
        return $fechaAntigua;
    }
    
    public function listaProcesosDisciplinariosDQL($strCodigoCentroCosto = "", $strIdentificacion = "", $strDesde = "", $strHasta = "") {        
        $em = $this->getEntityManager();
        $dql   = "SELECT pd, e FROM BrasaRecursoHumanoBundle:RhuDisciplinario pd JOIN pd.empleadoRel e WHERE pd.codigoDisciplinarioPk <> 0";
        if($strCodigoCentroCosto != "") {
            $dql .= " AND e.codigoCentroCostoFk = " . $strCodigoCentroCosto;
        }   
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion = '" . $strIdentificacion . "'";
        }
        if ($strDesde != ""){
            $dql .= " AND pd.fecha >='" . $strDesde . "'";
        }
        if($strHasta != "") {
            $dql .= " AND pd.fecha <='" .  $strHasta . "'";
        }
        return $dql;
    }
    
    public function eliminarDescargo($arrSeleccionados) {
        $em = $this->getEntityManager();
        if(count($arrSeleccionados) > 0) {
            foreach ($arrSeleccionados AS $codigoDescargo) {
                $arDescargo = $em->getRepository('BrasaRecursoHumanoBundle:RhuDisciplinarioDescargo')->find($codigoDescargo);
                $em->remove($arDescargo);       
            }
            $em->flush();
        }
    }
}