<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuDotacionCargoRepository extends EntityRepository {       
    public function listaDql($strCodigoCargo = "") {                
        $dql   = "SELECT dc FROM BrasaRecursoHumanoBundle:RhuDotacionCargo dc WHERE dc.codigoDotacionCargoPk <> 0 ";        
        if($strCodigoCargo != "") {
             $dql .= " AND dc.codigoCargoFk = " . $strCodigoCargo;
        }
        $dql .= " ORDER BY dc.codigoCargoFk";
        return $dql;
    }      
}