<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuEmpleadoEstudioRepository extends EntityRepository {
    
    public function listaDql($strIdentificacion = "", $strFecha = "", $strFechaAcreditacion = "" ) {        
        $em = $this->getEntityManager();
        $dql   = "SELECT ee, e FROM BrasaRecursoHumanoBundle:RhuEmpleadoEstudio ee JOIN ee.empleadoRel e WHERE ee.validarVencimiento = 1";
   
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion = '" . $strIdentificacion . "'";
        }
        if ($strFecha != ""){
            $dql .= " AND ee.fechaVencimientoCurso <='" . $strFecha . "'";
        }
        if ($strFechaAcreditacion != ""){
            $dql .= " AND ee.fechaVencimientoAcreditacion <='" . $strFechaAcreditacion . "'";
        }
        //$dql .= " ORDER BY p.empleadoRel.nombreCorto";
        return $dql;
    } 
    
    public function listaMovimientoDql($strIdentificacion = "", $strNombre = "", $strEstudio = "", $fechaVencimientoControl = "") {        
        $em = $this->getEntityManager();
        $dql   = "SELECT ee, e FROM BrasaRecursoHumanoBundle:RhuEmpleadoEstudio ee JOIN ee.empleadoRel e WHERE ee.codigoEmpleadoEstudioPk <> 0";
   
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion = '" . $strIdentificacion . "'";
        }
        if($strNombre != "" ) {
            $dql .= " AND e.nombreCorto LIKE '%" . $strNombre . "%'";
        }
        if($strEstudio != "") {
            $dql .= " AND ee.codigoEmpleadoEstudioTipoFk = " . $strEstudio;
        }       
        if($fechaVencimientoControl != "" ) {
            $dql .= " AND ee.fechaVencimientoCurso <='" . $fechaVencimientoControl . "'";
        }       
        $dql .= " ORDER BY ee.codigoEmpleadoEstudioPk desc";
        return $dql;
    }
}