<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuExamenCargoRepository extends EntityRepository {       
    public function listaDql($strCodigoCargo = "") {                
        $dql   = "SELECT ec FROM BrasaRecursoHumanoBundle:RhuExamenCargo ec WHERE ec.codigoExamenCargoPk <> 0 ";        
        if($strCodigoCargo != "") {
             $dql .= " AND ec.codigoCargoFk = " . $strCodigoCargo;
        }
        $dql .= " ORDER BY ec.codigoCargoFk";
        return $dql;
    }      
}