<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuIngresoBaseRepository extends EntityRepository {
    
    public function devuelveIbpFecha($codigoEmpleado, $fechaDesde, $fechaHasta, $codigoContrato) {
            $em = $this->getEntityManager();
            $dql   = "SELECT SUM(ib.vrIngresoBasePrestacion) as vrIngresoBasePrestacion FROM BrasaRecursoHumanoBundle:RhuIngresoBase ib "
                    . "WHERE ib.codigoEmpleadoFk = " . $codigoEmpleado . " "
                    . "AND ib.codigoContratoFk = " . $codigoContrato . " "
                    . "AND ib.fechaDesde >= '" . $fechaDesde . "' AND ib.fechaHasta <= '" . $fechaHasta . "'";
            $query = $em->createQuery($dql);            
            $floIbp = $query->getSingleScalarResult();
            if(!$floIbp) {
                $floIbp = 0;
            } 
            return $floIbp;
        }    
    
    public function listaDqlCostosIbc($strContrato = "", $strIdentificacion = "", $strDesde = "", $strHasta = "") {        
        $em = $this->getEntityManager();
        $dql   = "SELECT i, e FROM BrasaRecursoHumanoBundle:RhuIbc i JOIN i.empleadoRel e WHERE i.codigoIbcPk <> 0";
        if($strContrato != "") {
            $dql .= " AND i.codigoContratoFk = " . $strContrato;
        }   
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion = '" . $strIdentificacion . "'";
        }
        if ($strDesde != ""){
            $dql .= " AND i.fechaDesde >='" . date_format($strDesde, ('Y-m-d')). "'";
        }
        if($strHasta != "") {
            $dql .= " AND i.fechaHasta <='" . date_format($strHasta, ('Y-m-d')) . "'";
        }
        //$dql .= " ORDER BY p.empleadoRel.nombreCorto";
        return $dql;
    }         

    public function listaDql($strContrato = "", $strIdentificacion = "", $strDesde = "", $strHasta = "") {        
        $em = $this->getEntityManager();
        $dql   = "SELECT ib, e FROM BrasaRecursoHumanoBundle:RhuIngresoBase ib JOIN ib.empleadoRel e WHERE ib.codigoIngresoBasePk <> 0";
        if($strContrato != "") {
            $dql .= " AND ib.codigoContratoFk = " . $strContrato;
        }   
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion = '" . $strIdentificacion . "'";
        }
        if ($strDesde != ""){
            //$strDesde = new \DateTime($strDesde);
            $dql .= " AND ib.fechaDesde >='" . $strDesde . "'";
        }
        if($strHasta != "") {
            //$strHasta = new \DateTime($strHasta);
            $dql .= " AND ib.fechaHasta <='" .  $strHasta . "'";
        }
        //$dql .= " ORDER BY p.empleadoRel.nombreCorto";
        return $dql;
    }             
    
}