<?php

namespace Brasa\RecursoHumanoBundle\Repository;
use Doctrine\ORM\EntityRepository;

/**
 * RhuSubzonaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuSubzonaRepository extends EntityRepository {
    
    public function listaDQL($strNombre = "") {        
        $dql   = "SELECT s FROM BrasaRecursoHumanoBundle:RhuSubZona s WHERE s.codigoSubzonaPk <> 0";
        if($strNombre != "" ) {
            $dql .= " AND s.nombre LIKE '%" . $strNombre . "%'";
        }
        $dql .= " ORDER BY s.codigoSubzonaPk";
        return $dql;
    }
}
