<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuSucursalRepository extends EntityRepository {
   
     public function listaDql ($strNombre = "") {        
        $em = $this->getEntityManager();
        $dql   = "SELECT t, c FROM BrasaRecursoHumanoBundle:RhuSucursal t JOIN t.clienteRel c WHERE t.codigoClienteFk <> 0";
        if($strNombre != "") {
            $dql .= " AND t.nombre LIKE '%". $strNombre."%'";
          
        }   
       $dql .= " ORDER BY t.codigoSucursalPk ASC";
        return $dql;
    }
    
}