<?php

namespace Brasa\RecursoHumanoBundle\Repository;
use Doctrine\ORM\EntityRepository;

/**
 * RhuZonaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuZonaRepository extends EntityRepository {
    
    public function listaDQL($strNombre = "") {        
        $dql   = "SELECT z FROM BrasaRecursoHumanoBundle:RhuZona z WHERE z.codigoZonaPk <> 0";
        if($strNombre != "" ) {
            $dql .= " AND z.nombre LIKE '%" . $strNombre . "%'";
        }
        $dql .= " ORDER BY z.codigoZonaPk";
        return $dql;
    }
}
