<?php

namespace Brasa\TransporteBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * MovimientosRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TteNovedadRepository extends EntityRepository {    
    public function NovedadesGuiasDetalle($codigoGuia) {        
        $em = $this->getEntityManager();
        $dql   = "SELECT novedades FROM BrasaTransporteBundle:TteNovedad novedades WHERE novedades.codigoGuiaFk = " . $codigoGuia;
        $query = $em->createQuery($dql);        
        return $query;
    }    
    
    public function NovedadesPendientes() {        
        $em = $this->getEntityManager();
        $dql   = "SELECT novedades FROM BrasaTransporteBundle:TteNovedad novedades WHERE novedades.estadoSolucionada = 0";
        $query = $em->createQuery($dql);        
        return $query;
    }    
}