<?php

namespace Brasa\TransporteBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * MovimientosRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TteRelacionCumplidoRepository extends EntityRepository {

    public function Generar($codigoDespacho) {        
        $em = $this->getEntityManager();
        $arDespacho = new \Brasa\TransporteBundle\Entity\TteDespacho();
        $arDespacho = $em->getRepository('BrasaTransporteBundle:TteDespacho')->find($codigoDespacho);
        $arDespacho->setEstadoGenerado(1);
        $em->persist($arDespacho);
        $em->flush(); 
        return "";
    }    
    
    public function ListaRelacionesCumplidos($boolMostrarDescargadas, $codigoRelacionCumplidos, $fechaDesde, $fechaHasta) {        
        $em = $this->getEntityManager();
        $dql   = "SELECT relacionescumplidos FROM BrasaTransporteBundle:TteRelacionCumplido relacionescumplidos WHERE relacionescumplidos.codigoRelacionCumplidosPk <> 0";
        if($boolMostrarDescargadas != 1 ) {
            $dql .= " AND relacionescumplidos.estadoDescargada = 0";
        }

        if($codigoRelacionCumplidos != "" ) {
            $dql .= " AND relacionescumplidos.codigoRelacionCumplidosPk = " . $codigoRelacionCumplidos;
        }        

        if($fechaDesde != "" ) {
            $dql .= " AND relacionescumplidos.fecha >= '" . $fechaDesde->format('Y/m/d') . " 00:00:00'";
        }        
        if($fechaHasta != "" ) {
            $dql .= " AND relacionescumplidos.fecha <= '" . $fechaHasta->format('Y/m/d') . " 23:59:59'";
        }        
        $query = $em->createQuery($dql);        
        return $query;
    }            
}